package learn;

public class VariableCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10,b=20;
		System.out.println(a+b);
		//widening casting-converting given datatype to large datatype
		double d=a;
		System.out.println(d);
		//narrowing -converting large datatype to small datatype
		int e=(int)d;
		System.out.print(e);
		
		

	}

}
